'use strict';

var promisify = require('promisify-es6');

var multiaddr = require('multiaddr');

var PeerId = require('peer-id');

module.exports = function (send) {
  return promisify(function (opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    var verbose = opts.v || opts.verbose;
    send({
      path: 'swarm/peers',
      qs: opts
    }, function (err, result) {
      if (err) {
        return callback(err);
      } // go-ipfs <= 0.4.4


      if (result.Strings) {
        return callback(null, result.Strings.map(function (p) {
          var res = {};

          if (verbose) {
            var parts = p.split(' ');
            res.addr = multiaddr(parts[0]);
            res.latency = parts[1];
          } else {
            res.addr = multiaddr(p);
          }

          res.peer = PeerId.createFromB58String(res.addr.decapsulate('ipfs'));
          return res;
        }));
      } // go-ipfs >= 0.4.5


      callback(null, (result.Peers || []).map(function (p) {
        var res = {
          addr: multiaddr(p.Addr),
          peer: PeerId.createFromB58String(p.Peer),
          muxer: p.Muxer
        };

        if (p.Latency) {
          res.latency = p.Latency;
        }

        if (p.Streams) {
          res.streams = p.Streams;
        }

        return res;
      }));
    });
  });
};
