import React from 'react';
import { Button, List, Progress } from 'antd';
import './style/style.css'
import logo from '../META_Logo_black.svg';
import { testData } from './test/testData'

class Authority extends React.Component {
    data = {
        authorityItems: [],
    }

    state = {
        getAuthorityInfo: false,
    }

    constructor(props) {
        super(props)
        this.getAuthorityList()
    }

    onApplyBtnClick() {
        console.log('onApplyBtnClick');
    }

    getAuthorityList() {
        let list = []
        console.log('getAuthorityList length: ', testData.govTestData.length);

        testData.govTestData.map(item => {
            list.push(
                <div className = 'authorityComp'>
                    <div style={{float: 'left', width: '19%', backgroundColor: '#FFEAF6'}}>
                        <img src={logo} alt='' />
                    </div>
                    <div style={{padding: 30, float: 'left',width: '81%'}}>
                        <div style={{height: '70px'}}>
                            <h2 style={{float: 'left'}}>{item.company}</h2>
                            <h4 style={{float: 'right'}}>Address: {item.propasalAddr}</h4>
                        </div>
                        <div style={{height: '80px'}}><p>{item.description}</p></div>
                        <div style={{height: '80px'}}>
                            <div><h3><a href="http://www.metadium.com" target='_blank'>{item.company} Web site</a></h3></div>
                            <div>
                                {this.getSNSList}
                            </div>
                        </div>
                    </div>
                </div>
            )
        })
    }

    getSNSList(snsList) {
        let sns = []
        for (key in snsList) {
            <Button className='snsGroup'>{snsList[key]}</Button>
        }
        return sns;
    }


    render() {
        return (
            <div>
                <div className='contentDiv'>
                    <div><Button id='applyBtn' onClick={this.onApplyBtnClick}>Apply for Authority</Button></div>
                    <div style={{ padding: 20, minHeight: 500 }}>
                        <div className = 'authorityComp'>
                            <div style={{float: 'left', width: '19%', backgroundColor: '#FFEAF6'}}>
                                <img src={logo} alt='' />
                            </div>
                            <div style={{padding: 30, float: 'left',width: '81%'}}>
                                <div style={{height: '70px'}}>
                                    <h2 style={{float: 'left'}}>Metadium</h2>
                                    <h4 style={{float: 'right'}}>Address: 0xcd93366D12931BeAbfBF892d1b0111D6573Ac0e0</h4>
                                </div>
                                <div style={{height: '80px'}}><p>NEXT -Â GENERATION IDENTITY PROTOCOL. Optimally Manage, Use and Protect Your Personal Identity</p></div>
                                <div style={{height: '80px'}}>
                                    <div><h3><a href="http://www.metadium.com" target='_blank'>Metadium Web site</a></h3></div>
                                    <div>
                                        <Button className='snsGroup'>Twitter</Button>
                                        <Button className='snsGroup'>Facebook</Button>
                                        <Button className='snsGroup'>Telegram</Button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export { Authority }
