'use strict';

var promisify = require('promisify-es6');

var _ = require('lodash');

module.exports = function (send) {
  return promisify(function (hash, opts, callback) {
    if (typeof hash === 'function') {
      callback = hash;
      opts = null;
      hash = null;
    }

    if (typeof opts === 'function') {
      callback = opts;
    }

    if (hash && hash.type) {
      opts = hash;
      hash = null;
    } else {
      opts = null;
    }

    send({
      path: 'pin/ls',
      args: hash,
      qs: opts
    }, function (err, res) {
      if (err) {
        return callback(err);
      }

      callback(null, _.keys(res.Keys).map(function (hash) {
        return {
          hash: hash,
          type: res.Keys[hash].Type
        };
      }));
    });
  });
};
