'use strict';

var promisify = require('promisify-es6');

var dagPB = require('ipld-dag-pb');

var DAGNode = dagPB.DAGNode;
var DAGLink = dagPB.DAGLink;

var bs58 = require('bs58');

var CID = require('cids');

var LRU = require('lru-cache');

var lruOptions = {
  max: 128
};
var cache = LRU(lruOptions);

module.exports = function (send) {
  return promisify(function (cid, options, callback) {
    if (typeof options === 'function') {
      callback = options;
      options = {};
    }

    if (!options) {
      options = {};
    }

    var cidB58Str;

    try {
      cid = new CID(cid);
      cidB58Str = cid.toBaseEncodedString();
    } catch (err) {
      return callback(err);
    }

    var node = cache.get(cidB58Str);

    if (node) {
      return callback(null, node);
    }

    send({
      path: 'object/get',
      args: cidB58Str,
      qs: {
        'data-encoding': 'base64'
      }
    }, function (err, result) {
      if (err) {
        return callback(err);
      }

      result.Data = Buffer.from(result.Data, 'base64');
      var links = result.Links.map(function (l) {
        return new DAGLink(l.Name, l.Size, Buffer.from(bs58.decode(l.Hash)));
      });
      DAGNode.create(result.Data, links, function (err, node) {
        if (err) {
          return callback(err);
        }

        cache.set(cidB58Str, node);
        callback(null, node);
      });
    });
  });
};
