'use strict';

var nodeify = require('../nodeify');

var webcrypto = require('../webcrypto.js')();

exports.utils = require('./rsa-utils');

exports.generateKey = function (bits, callback) {
  nodeify(webcrypto.subtle.generateKey({
    name: 'RSASSA-PKCS1-v1_5',
    modulusLength: bits,
    publicExponent: new Uint8Array([0x01, 0x00, 0x01]),
    hash: {
      name: 'SHA-256'
    }
  }, true, ['sign', 'verify']).then(exportKey).then(function (keys) {
    return {
      privateKey: keys[0],
      publicKey: keys[1]
    };
  }), callback);
}; // Takes a jwk key


exports.unmarshalPrivateKey = function (key, callback) {
  var privateKey = webcrypto.subtle.importKey('jwk', key, {
    name: 'RSASSA-PKCS1-v1_5',
    hash: {
      name: 'SHA-256'
    }
  }, true, ['sign']);
  nodeify(Promise.all([privateKey, derivePublicFromPrivate(key)]).then(function (keys) {
    return exportKey({
      privateKey: keys[0],
      publicKey: keys[1]
    });
  }).then(function (keys) {
    return {
      privateKey: keys[0],
      publicKey: keys[1]
    };
  }), callback);
};

exports.getRandomValues = function (arr) {
  return Buffer.from(webcrypto.getRandomValues(arr));
};

exports.hashAndSign = function (key, msg, callback) {
  nodeify(webcrypto.subtle.importKey('jwk', key, {
    name: 'RSASSA-PKCS1-v1_5',
    hash: {
      name: 'SHA-256'
    }
  }, false, ['sign']).then(function (privateKey) {
    return webcrypto.subtle.sign({
      name: 'RSASSA-PKCS1-v1_5'
    }, privateKey, Uint8Array.from(msg));
  }).then(function (sig) {
    return Buffer.from(sig);
  }), callback);
};

exports.hashAndVerify = function (key, sig, msg, callback) {
  nodeify(webcrypto.subtle.importKey('jwk', key, {
    name: 'RSASSA-PKCS1-v1_5',
    hash: {
      name: 'SHA-256'
    }
  }, false, ['verify']).then(function (publicKey) {
    return webcrypto.subtle.verify({
      name: 'RSASSA-PKCS1-v1_5'
    }, publicKey, sig, msg);
  }), callback);
};

function exportKey(pair) {
  return Promise.all([webcrypto.subtle.exportKey('jwk', pair.privateKey), webcrypto.subtle.exportKey('jwk', pair.publicKey)]);
}

function derivePublicFromPrivate(jwKey) {
  return webcrypto.subtle.importKey('jwk', {
    kty: jwKey.kty,
    n: jwKey.n,
    e: jwKey.e
  }, {
    name: 'RSASSA-PKCS1-v1_5',
    hash: {
      name: 'SHA-256'
    }
  }, true, ['verify']);
}
